import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { 
  User, 
  GitBranch, 
  MessageCircle, 
  Calendar, 
  TrendingUp, 
  Activity,
  GitCommit,
  GitPullRequest,
  Star,
  Eye,
  Clock,
  ChartBarHorizontal,
  MagnifyingGlass,
  GithubLogo,
  Warning,
  ThumbsUp,
  Lightbulb,
  Heart,
  Fire,
  Code
} from '@phosphor-icons/react';

// Styled Components
const Container = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
`;

const Header = styled.header`
  padding: 2rem;
  text-align: center;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
`;

const Title = styled.h1`
  font-size: 3rem;
  margin: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 1rem;
`;

const SearchSection = styled.div`
  padding: 2rem;
  display: flex;
  justify-content: center;
  gap: 1rem;
  flex-wrap: wrap;
`;

const SearchInput = styled.input`
  padding: 1rem 1.5rem;
  font-size: 1.1rem;
  border: none;
  border-radius: 8px;
  background: rgba(255, 255, 255, 0.9);
  color: #333;
  min-width: 300px;

  &:focus {
    outline: none;
    box-shadow: 0 0 0 3px rgba(255, 255, 255, 0.3);
  }
`;

const SearchButton = styled.button`
  padding: 1rem 2rem;
  font-size: 1.1rem;
  border: none;
  border-radius: 8px;
  background: #28a745;
  color: white;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  transition: all 0.3s ease;

  &:hover {
    background: #218838;
    transform: translateY(-2px);
  }

  &:disabled {
    background: #6c757d;
    cursor: not-allowed;
    transform: none;
  }
`;

const Dashboard = styled.div`
  padding: 2rem;
  max-width: 1400px;
  margin: 0 auto;
`;

const MetricsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  margin-bottom: 2rem;
`;

const MetricCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 12px;
  padding: 2rem;
  border: 1px solid rgba(255, 255, 255, 0.2);
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-5px);
  }
`;

const MetricHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 1rem;
`;

const MetricValue = styled.div`
  font-size: 2.5rem;
  font-weight: bold;
  margin-bottom: 0.5rem;
`;

const MetricLabel = styled.div`
  font-size: 1.1rem;
  opacity: 0.8;
`;

const UserProfile = styled.div`
  display: flex;
  align-items: center;
  gap: 2rem;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 12px;
  padding: 2rem;
  margin-bottom: 2rem;
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const Avatar = styled.img`
  width: 100px;
  height: 100px;
  border-radius: 50%;
  border: 3px solid rgba(255, 255, 255, 0.3);
`;

const UserInfo = styled.div`
  flex: 1;
`;

const UserName = styled.h2`
  margin: 0 0 0.5rem 0;
  font-size: 2rem;
`;

const UserBio = styled.p`
  margin: 0.5rem 0;
  opacity: 0.8;
`;

const UserStats = styled.div`
  display: flex;
  gap: 2rem;
  margin-top: 1rem;
`;

const UserStat = styled.div`
  text-align: center;
`;

const StatValue = styled.div`
  font-size: 1.5rem;
  font-weight: bold;
`;

const StatLabel = styled.div`
  font-size: 0.9rem;
  opacity: 0.8;
`;

const LoadingSpinner = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 4rem;
  font-size: 1.2rem;
`;

const ErrorMessage = styled.div`
  background: rgba(220, 53, 69, 0.1);
  border: 1px solid rgba(220, 53, 69, 0.3);
  border-radius: 8px;
  padding: 1rem;
  margin: 2rem;
  text-align: center;
  color: #f8d7da;
`;

const AnalysisSection = styled.div`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 12px;
  padding: 2rem;
  margin-bottom: 2rem;
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const SectionTitle = styled.h3`
  margin: 0 0 1rem 0;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const InsightsList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
`;

const InsightItem = styled.li`
  padding: 0.75rem 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);

  &:last-child {
    border-bottom: none;
  }
`;

function App() {
  const [username, setUsername] = useState('');
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [metrics, setMetrics] = useState(null);

  const fetchUserData = async () => {
    if (!username.trim()) return;

    setLoading(true);
    setError(null);

    try {
      // Fetch user profile
      const userResponse = await fetch(`https://api.github.com/users/${username}`);
      if (!userResponse.ok) {
        throw new Error('User not found');
      }
      const user = await userResponse.json();

      // Fetch user repositories
      const reposResponse = await fetch(`https://api.github.com/users/${username}/repos?per_page=100`);
      const repos = await reposResponse.json();

      // Calculate metrics
      const totalStars = repos.reduce((sum, repo) => sum + repo.stargazers_count, 0);
      const totalForks = repos.reduce((sum, repo) => sum + repo.forks_count, 0);
      const languages = {};

      repos.forEach(repo => {
        if (repo.language) {
          languages[repo.language] = (languages[repo.language] || 0) + 1;
        }
      });

      const topLanguage = Object.entries(languages)
        .sort((a, b) => b[1] - a[1])[0]?.[0] || 'Unknown';

      // Calculate activity patterns
      const recentRepos = repos.filter(repo => {
        const updatedAt = new Date(repo.updated_at);
        const sixMonthsAgo = new Date();
        sixMonthsAgo.setMonth(sixMonthsAgo.getMonth() - 6);
        return updatedAt > sixMonthsAgo;
      });

      setUserData(user);
      setMetrics({
        totalRepos: repos.length,
        totalStars,
        totalForks,
        topLanguage,
        activeRepos: recentRepos.length,
        avgStarsPerRepo: repos.length > 0 ? (totalStars / repos.length).toFixed(1) : 0,
        languages: Object.keys(languages).length,
        insights: generateInsights(user, repos, languages)
      });

    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const generateInsights = (user, repos, languages) => {
    const insights = [];

    if (repos.length > 50) {
      insights.push('üöÄ Highly productive developer with extensive repository portfolio');
    }

    if (Object.keys(languages).length > 5) {
      insights.push('üåü Polyglot programmer with diverse language experience');
    }

    const recentActivity = repos.filter(repo => {
      const updatedAt = new Date(repo.updated_at);
      const monthAgo = new Date();
      monthAgo.setMonth(monthAgo.getMonth() - 1);
      return updatedAt > monthAgo;
    });

    if (recentActivity.length > 3) {
      insights.push('‚ö° Very active recently with multiple repository updates');
    }

    const totalStars = repos.reduce((sum, repo) => sum + repo.stargazers_count, 0);
    if (totalStars > 100) {
      insights.push('‚≠ê Creates popular projects that attract community attention');
    }

    if (user.followers > user.following) {
      insights.push('üë• Strong community influence with more followers than following');
    }

    const hasDescriptions = repos.filter(repo => repo.description).length;
    if (hasDescriptions / repos.length > 0.8) {
      insights.push('üìù Good documentation practices with detailed repository descriptions');
    }

    return insights;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchUserData();
  };

  return (
    <Container>
      <Header>
        <Title>
          <Github size={48} />
          Reflectocat
        </Title>
        <p>GitHub User Communication Pattern Analyzer</p>
      </Header>

      <SearchSection>
        <form onSubmit={handleSubmit} style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap', justifyContent: 'center' }}>
          <SearchInput
            type="text"
            placeholder="Enter GitHub username..."
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <SearchButton type="submit" disabled={loading}>
            <Search size={20} />
            {loading ? 'Analyzing...' : 'Analyze'}
          </SearchButton>
        </form>
      </SearchSection>

      {loading && (
        <LoadingSpinner>
          <Activity size={24} style={{ marginRight: '0.5rem' }} />
          Fetching GitHub data and analyzing patterns...
        </LoadingSpinner>
      )}

      {error && (
        <ErrorMessage>
          Error: {error}
        </ErrorMessage>
      )}

      {userData && metrics && (
        <Dashboard>
          <UserProfile>
            <Avatar src={userData.avatar_url} alt={userData.name || userData.login} />
            <UserInfo>
              <UserName>{userData.name || userData.login}</UserName>
              {userData.bio && <UserBio>{userData.bio}</UserBio>}
              {userData.location && <UserBio>üìç {userData.location}</UserBio>}
              {userData.company && <UserBio>üè¢ {userData.company}</UserBio>}
              <UserStats>
                <UserStat>
                  <StatValue>{userData.followers}</StatValue>
                  <StatLabel>Followers</StatLabel>
                </UserStat>
                <UserStat>
                  <StatValue>{userData.following}</StatValue>
                  <StatLabel>Following</StatLabel>
                </UserStat>
                <UserStat>
                  <StatValue>{userData.public_repos}</StatValue>
                  <StatLabel>Public Repos</StatLabel>
                </UserStat>
              </UserStats>
            </UserInfo>
          </UserProfile>

          <MetricsGrid>
            <MetricCard>
              <MetricHeader>
                <GitBranch size={24} />
                <h3>Repository Activity</h3>
              </MetricHeader>
              <MetricValue>{metrics.totalRepos}</MetricValue>
              <MetricLabel>Total Repositories</MetricLabel>
            </MetricCard>

            <MetricCard>
              <MetricHeader>
                <Star size={24} />
                <h3>Community Impact</h3>
              </MetricHeader>
              <MetricValue>{metrics.totalStars}</MetricValue>
              <MetricLabel>Total Stars Received</MetricLabel>
            </MetricCard>

            <MetricCard>
              <MetricHeader>
                <GitCommit size={24} />
                <h3>Code Quality</h3>
              </MetricHeader>
              <MetricValue>{metrics.avgStarsPerRepo}</MetricValue>
              <MetricLabel>Average Stars per Repo</MetricLabel>
            </MetricCard>

            <MetricCard>
              <MetricHeader>
                <BarChart3 size={24} />
                <h3>Technology Stack</h3>
              </MetricHeader>
              <MetricValue>{metrics.topLanguage}</MetricValue>
              <MetricLabel>Primary Language</MetricLabel>
            </MetricCard>

            <MetricCard>
              <MetricHeader>
                <TrendingUp size={24} />
                <h3>Recent Activity</h3>
              </MetricHeader>
              <MetricValue>{metrics.activeRepos}</MetricValue>
              <MetricLabel>Active Repos (6 months)</MetricLabel>
            </MetricCard>

            <MetricCard>
              <MetricHeader>
                <MessageCircle size={24} />
                <h3>Language Diversity</h3>
              </MetricHeader>
              <MetricValue>{metrics.languages}</MetricValue>
              <MetricLabel>Programming Languages</MetricLabel>
            </MetricCard>
          </MetricsGrid>

          <AnalysisSection>
            <SectionTitle>
              <Activity size={24} />
              Behavioral Insights
            </SectionTitle>
            <InsightsList>
              {metrics.insights.map((insight, index) => (
                <InsightItem key={index}>{insight}</InsightItem>
              ))}
            </InsightsList>
          </AnalysisSection>
        </Dashboard>
      )}
    </Container>
  );
}

export default App;
