import React, { useState } from 'react';
import styled from 'styled-components';
import { 
  User, 
  GitBranch, 
  Calendar, 
  TrendUp, 
  ActivityIcon,
  Star,
  Clock,
  ChartBarHorizontal,
  MagnifyingGlass,
  GithubLogo,
  Heart,
  Users,
  BookOpen,
  Warning,
  ThumbsUp,
  Lightbulb,
  LineChart,
  ChatCircle,
  Brain
} from '@phosphor-icons/react';

// Styled Components
const Container = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%);
  color: white;
  font-family: 'Inter', 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
`;

const Header = styled.header`
  padding: 2rem;
  text-align: center;
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(20px);
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
`;

const Title = styled.h1`
  font-size: 3.5rem;
  margin: 0 0 1rem 0;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 1rem;
  font-weight: 700;
  background: linear-gradient(45deg, #4facfe, #00f2fe);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const Subtitle = styled.p`
  font-size: 1.2rem;
  color: rgba(255, 255, 255, 0.8);
  margin: 0 auto;
  max-width: 600px;
`;

const SearchSection = styled.div`
  padding: 3rem 2rem;
  display: flex;
  justify-content: center;
  gap: 1rem;
  flex-wrap: wrap;
`;

const SearchContainer = styled.div`
  position: relative;
  display: flex;
  align-items: center;
`;

const SearchInput = styled.input`
  padding: 1.2rem 1.5rem 1.2rem 3rem;
  font-size: 1.1rem;
  border: 2px solid rgba(255, 255, 255, 0.2);
  border-radius: 12px;
  background: rgba(255, 255, 255, 0.1);
  color: white;
  min-width: 350px;
  backdrop-filter: blur(10px);
  transition: all 0.3s ease;

  &::placeholder {
    color: rgba(255, 255, 255, 0.6);
  }

  &:focus {
    outline: none;
    border-color: #4facfe;
    box-shadow: 0 0 0 3px rgba(79, 172, 254, 0.3);
    background: rgba(255, 255, 255, 0.15);
  }
`;

const SearchIcon = styled(MagnifyingGlass)`
  position: absolute;
  left: 1rem;
  color: rgba(255, 255, 255, 0.6);
  z-index: 1;
`;

const SearchButton = styled.button`
  padding: 1.2rem 2rem;
  font-size: 1.1rem;
  border: none;
  border-radius: 12px;
  background: linear-gradient(45deg, #4facfe, #00f2fe);
  color: white;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-weight: 600;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(79, 172, 254, 0.4);
  }

  &:disabled {
    background: rgba(255, 255, 255, 0.2);
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
  }
`;

const Dashboard = styled.div`
  padding: 2rem;
  max-width: 1400px;
  margin: 0 auto;
`;

const Card = styled.div`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 16px;
  padding: 2rem;
  margin-bottom: 2rem;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-4px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
  }
`;

const UserProfile = styled.div`
  display: flex;
  gap: 2rem;
  align-items: center;
  margin-bottom: 2rem;

  @media (max-width: 768px) {
    flex-direction: column;
    text-align: center;
  }
`;

const Avatar = styled.img`
  width: 120px;
  height: 120px;
  border-radius: 50%;
  border: 4px solid rgba(255, 255, 255, 0.3);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
`;

const UserInfo = styled.div`
  flex: 1;
`;

const UserName = styled.h2`
  font-size: 2rem;
  margin: 0 0 0.5rem 0;
  color: white;
`;

const UserBio = styled.p`
  color: rgba(255, 255, 255, 0.8);
  margin: 0 0 1rem 0;
  line-height: 1.6;
`;

const UserStats = styled.div`
  display: flex;
  gap: 2rem;

  @media (max-width: 768px) {
    justify-content: center;
  }
`;

const Stat = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: rgba(255, 255, 255, 0.8);
`;

const MetricsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  margin-bottom: 3rem;
`;

const MetricCard = styled(Card)`
  text-align: center;
  padding: 2rem 1.5rem;
`;

const MetricValue = styled.div`
  font-size: 3rem;
  font-weight: 700;
  margin: 1rem 0;
  background: linear-gradient(45deg, #4facfe, #00f2fe);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const MetricLabel = styled.div`
  color: rgba(255, 255, 255, 0.8);
  font-size: 1.1rem;
  margin-bottom: 0.5rem;
`;

const MetricIcon = styled.div`
  display: flex;
  justify-content: center;
  margin-bottom: 1rem;
  color: #4facfe;
`;

const LoadingSpinner = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 4rem;

  &::after {
    content: '';
    width: 40px;
    height: 40px;
    border: 3px solid rgba(255, 255, 255, 0.3);
    border-top: 3px solid #4facfe;
    border-radius: 50%;
    animation: spin 1s linear infinite;
  }

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const ErrorMessage = styled.div`
  background: rgba(220, 53, 69, 0.2);
  border: 1px solid rgba(220, 53, 69, 0.4);
  color: #ff6b7a;
  padding: 1.5rem;
  border-radius: 12px;
  text-align: center;
  margin: 2rem 0;
`;

const RepositoryGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
  gap: 1.5rem;
  margin-top: 2rem;
`;

const RepoCard = styled.div`
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  padding: 1.5rem;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.1);
    transform: translateY(-2px);
  }
`;

const RepoName = styled.h3`
  margin: 0 0 0.5rem 0;
  color: #4facfe;
  font-size: 1.1rem;
`;

const RepoDescription = styled.p`
  color: rgba(255, 255, 255, 0.8);
  margin: 0 0 1rem 0;
  font-size: 0.9rem;
  line-height: 1.4;
`;

const RepoStats = styled.div`
  display: flex;
  gap: 1rem;
  font-size: 0.8rem;
  color: rgba(255, 255, 255, 0.6);
`;

const RepoStat = styled.div`
  display: flex;
  align-items: center;
  gap: 0.3rem;
`;

const SectionTitle = styled.h2`
  font-size: 1.8rem;
  margin: 3rem 0 1.5rem 0;
  color: white;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const FeatureCard = styled(Card)`
  text-align: center;
  padding: 3rem 2rem;
`;

const FeatureIcon = styled.div`
  font-size: 4rem;
  margin-bottom: 1rem;
  color: #4facfe;
`;

const FeatureTitle = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 1rem;
  color: white;
`;

const FeatureDescription = styled.p`
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.6;
`;

// ----------- ANALYSIS HELPERS ------------

function analyzeText(text) {
  if (!text || typeof text !== "string" || text.trim().length === 0) {
    return {
      hostility: 0,
      formality: 0,
      sentiment: 0,
      toxicity: 0,
      constructiveness: 0,
      keywords: {},
      patterns: []
    };
  }

  const hostileWords = ["terrible", "wrong", "stupid", "awful", "hate", "useless", "idiot", "ridiculous"];
  const formalWords = ["therefore", "consequently", "furthermore", "however", "regards", "additionally"];
  const positiveWords = ["thanks", "great", "awesome", "helpful", "appreciate", "good", "excellent"];
  const negativeWords = ["bug", "issue", "problem", "error", "fail", "broken", "bad"];
  const toxicWords = ["stupid", "idiot", "useless", "terrible", "hate", "awful"];
  const constructiveWords = ["suggest", "perhaps", "consider", "improve", "help", "could", "maybe"];

  const words = text.toLowerCase().split(/\W+/).filter(Boolean);
  const wordCount = words.length || 1;

  const sentences = text.split(/[.!?]+/).filter(s => s.trim().length > 0);

  const hostility = words.filter(w => hostileWords.includes(w)).length / wordCount;
  const formality = words.filter(w => formalWords.includes(w)).length / wordCount;
  const sentiment =
    (words.filter(w => positiveWords.includes(w)).length -
      words.filter(w => negativeWords.includes(w)).length) / wordCount;
  const toxicity = words.filter(w => toxicWords.includes(w)).length / wordCount;
  const constructiveness = words.filter(w => constructiveWords.includes(w)).length / wordCount;

  // Detect patterns
  const patterns = [];
  if (sentences.length > 0) {
    if (sentences.filter(s => s.includes("?")).length / sentences.length > 0.3) {
      patterns.push("Frequently asks questions - shows engagement");
    }
    if (constructiveness > 0.1) {
      patterns.push("Often provides constructive feedback");
    }
    if (hostility > 0.1) {
      patterns.push("Shows signs of confrontational communication");
    }
    if (formality > 0.15) {
      patterns.push("Maintains professional tone");
    }
  }

  // Extract topic keywords
  const stopWords = new Set(["the", "a", "an", "and", "or", "but", "in", "on", "at", "to", "for"]);
  const keywords = words
    .filter(w => w.length > 3 && !stopWords.has(w))
    .reduce((acc, word) => {
      acc[word] = (acc[word] || 0) + 1;
      return acc;
    }, {});

  return {
    hostility,
    formality,
    sentiment,
    toxicity,
    constructiveness,
    keywords,
    patterns
  };
}

function analyzeBio(bio) {
  if (!bio) return null;
  return analyzeText(bio);
}

function generateCommunicationMetrics(userData, reposData) {
  // Generate mock communication metrics based on user data
  const repoCount = userData.public_repos || 0;
  const followerCount = userData.followers || 0;

  // Mock metrics with some logic based on actual data
  const toxicity = Math.max(0, Math.min(0.15, (repoCount > 50 ? 0.02 : 0.05) + Math.random() * 0.03));
  const constructiveness = Math.min(1, (followerCount > 100 ? 0.8 : 0.6) + Math.random() * 0.2);
  const overallTone = Math.random() * 0.4 - 0.1; // -0.1 to +0.3

  return {
    toxicity,
    constructiveness,
    overallTone
  };
}

function generateBehavioralPatterns(userData, reposData, eventsData) {
  const languages = reposData.map(repo => repo.language).filter(Boolean);
  const languageCounts = languages.reduce((acc, lang) => {
    acc[lang] = (acc[lang] || 0) + 1;
    return acc;
  }, {});

  const topLanguages = Object.entries(languageCounts)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 3)
    .map(([lang]) => lang);

  const patterns = [];
  if (userData.public_repos > 50) {
    patterns.push("Highly productive contributor");
  }
  if (userData.followers > userData.following) {
    patterns.push("Well-respected community member");
  }
  if (topLanguages.length > 2) {
    patterns.push("Polyglot programmer");
  }

  const topics = topLanguages.concat(['open-source', 'collaboration', 'development']);

  return {
    communicationStyle: patterns,
    topTopics: topics,
    insights: [
      "Active in open source community",
      "Collaborative development approach",
      "Focus on code quality and best practices"
    ]
  };
}

function processRecentActivity(eventsData) {
  return eventsData.slice(0, 10).map(event => {
    let content = "";
    let type = event.type;

    switch (event.type) {
      case 'PushEvent':
        content = `Pushed ${event.payload.commits?.length || 0} commits`;
        break;
      case 'CreateEvent':
        content = `Created ${event.payload.ref_type}`;
        break;
      case 'IssuesEvent':
        content = `${event.payload.action} issue`;
        break;
      case 'PullRequestEvent':
        content = `${event.payload.action} pull request`;
        break;
      default:
        content = type.replace('Event', '');
    }

    return {
      type,
      content,
      repo: event.repo.name,
      createdAt: event.created_at,
      url: `https://github.com/${event.repo.name}`
    };
  });
}

