function App() {
  const [username, setUsername] = useState('');
  const [userData, setUserData] = useState(null);
  const [repositories, setRepositories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [analysisData, setAnalysisData] = useState(null);

  const handleSearch = async () => {
    if (!username.trim()) return;

    setLoading(true);
    setError('');
    setUserData(null);
    setRepositories([]);
    setAnalysisData(null);

    try {
      // Fetch real user data from GitHub API
      const userResponse = await fetch(`https://api.github.com/users/${username.trim()}`);

      if (!userResponse.ok) {
        throw new Error('User not found');
      }

      const userData = await userResponse.json();

      // Fetch user's repositories
      const reposResponse = await fetch(`https://api.github.com/users/${username.trim()}/repos?sort=updated&per_page=20`);
      const reposData = await reposResponse.json();

      // Fetch recent events/activity
      const eventsResponse = await fetch(`https://api.github.com/users/${username.trim()}/events?per_page=30`);
      const eventsData = await eventsResponse.json();

      // Generate analysis data
      const analysis = {
        bioAnalysis: analyzeBio(userData.bio),
        communicationMetrics: generateCommunicationMetrics(userData, reposData),
        behavioralPatterns: generateBehavioralPatterns(userData, reposData, eventsData),
        recentActivity: processRecentActivity(eventsData)
      };

      setUserData(userData);
      setRepositories(reposData);
      setAnalysisData(analysis);
      setLoading(false);

    } catch (err) {
      setError(err.message === 'User not found' ? 'User not found' : 'API error occurred');
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const getLanguageColor = (language) => {
    const colors = {
      JavaScript: '#f1e05a',
      HTML: '#e34c26',
      CSS: '#563d7c',
      Python: '#3572A5',
      Java: '#b07219',
      Ruby: '#701516',
      Go: '#00ADD8',
      TypeScript: '#2b7489',
      PHP: '#4F5D95'
    };
    return colors[language] || '#ffffff';
  };

  return (
    <Container>
      <Header>
        <Title>
          <GithubLogo size={48} />
          Reflectocat
        </Title>
        <Subtitle>
          Discover and analyze GitHub profiles with beautiful insights and metrics
        </Subtitle>
      </Header>

      <SearchSection>
        <SearchContainer>
          <SearchIcon size={20} />
          <SearchInput
            type="text"
            placeholder="Enter GitHub username..."
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            onKeyPress={handleKeyPress}
          />
        </SearchContainer>
        <SearchButton onClick={handleSearch} disabled={loading}>
          {loading ? 'Searching...' : 'Analyze Profile'}
          <ActivityIcon size={20} />
        </SearchButton>
      </SearchSection>

      <Dashboard>
        {loading && <LoadingSpinner />}

        {error && <ErrorMessage>{error}</ErrorMessage>}

        {!userData && !loading && !error && (
          <MetricsGrid>
            <FeatureCard>
              <FeatureIcon>
                <User size={64} />
              </FeatureIcon>
              <FeatureTitle>Profile Analysis</FeatureTitle>
              <FeatureDescription>
                Get comprehensive insights into any GitHub user's profile, including bio analysis, stats, and activity patterns.
              </FeatureDescription>
            </FeatureCard>

            <FeatureCard>
              <FeatureIcon>
                <ChartBarHorizontal size={64} />
              </FeatureIcon>
              <FeatureTitle>Repository Metrics</FeatureTitle>
              <FeatureDescription>
                Analyze repository statistics, languages used, star counts, and contribution patterns across projects.
              </FeatureDescription>
            </FeatureCard>

            <FeatureCard>
              <FeatureIcon>
                <TrendUp size={64} />
              </FeatureIcon>
              <FeatureTitle>Activity Tracking</FeatureTitle>
              <FeatureDescription>
                Track commits, issues, pull requests, and other activities to understand development patterns.
              </FeatureDescription>
            </FeatureCard>
          </MetricsGrid>
        )}

        {userData && (
          <>
            <Card>
              <UserProfile>
                <Avatar src={userData.avatar_url} alt={`${userData.login}'s avatar`} />
                <UserInfo>
                  <UserName>{userData.name || userData.login}</UserName>
                  <UserBio>{userData.bio || 'No bio available'}</UserBio>
                  <UserStats>
                    <Stat>
                      <Users size={16} />
                      {userData.followers} followers
                    </Stat>
                    <Stat>
                      <Heart size={16} />
                      {userData.following} following
                    </Stat>
                    <Stat>
                      <BookOpen size={16} />
                      {userData.public_repos} repos
                    </Stat>
                    <Stat>
                      <Calendar size={16} />
                      Joined {formatDate(userData.created_at)}
                    </Stat>
                  </UserStats>
                </UserInfo>
              </UserProfile>
            </Card>

            <MetricsGrid>
              <MetricCard>
                <MetricIcon>
                  <Star size={32} />
                </MetricIcon>
                <MetricLabel>Total Stars</MetricLabel>
                <MetricValue>
                  {repositories.reduce((sum, repo) => sum + repo.stargazers_count, 0).toLocaleString()}
                </MetricValue>
              </MetricCard>

              <MetricCard>
                <MetricIcon>
                  <GitBranch size={32} />
                </MetricIcon>
                <MetricLabel>Total Forks</MetricLabel>
                <MetricValue>
                  {repositories.reduce((sum, repo) => sum + repo.forks_count, 0).toLocaleString()}
                </MetricValue>
              </MetricCard>

              <MetricCard>
                <MetricIcon>
                  <BookOpen size={32} />
                </MetricIcon>
                <MetricLabel>Public Repositories</MetricLabel>
                <MetricValue>{userData.public_repos}</MetricValue>
              </MetricCard>

              <MetricCard>
                <MetricIcon>
                  <Users size={32} />
                </MetricIcon>
                <MetricLabel>Followers</MetricLabel>
                <MetricValue>{userData.followers.toLocaleString()}</MetricValue>
              </MetricCard>
            </MetricsGrid>

            <Card>
              <SectionTitle>
                <BookOpen size={24} />
                Popular Repositories
              </SectionTitle>
              <RepositoryGrid>
                {repositories.map((repo, index) => (
                  <RepoCard key={index}>
                    <RepoName>{repo.name}</RepoName>
                    <RepoDescription>
                      {repo.description || 'No description available'}
                    </RepoDescription>
                    <RepoStats>
                      <RepoStat>
                        <Star size={14} />
                        {repo.stargazers_count.toLocaleString()}
                      </RepoStat>
                      <RepoStat>
                        <GitBranch size={14} />
                        {repo.forks_count.toLocaleString()}
                      </RepoStat>
                      {repo.language && (
                        <RepoStat>
                          <div 
                            style={{
                              width: '12px',
                              height: '12px',
                              borderRadius: '50%',
                              backgroundColor: getLanguageColor(repo.language)
                            }}
                          />
                          {repo.language}
                        </RepoStat>
                      )}
                      <RepoStat>
                        <Clock size={14} />
                        {formatDate(repo.updated_at)}
                      </RepoStat>
                    </RepoStats>
                  </RepoCard>
                ))}
              </RepositoryGrid>
            </Card>
          </>
        )}
      </Dashboard>
    </Container>
  );
}

export default App;
